INSERT INTO PORTAL.COURSE (TITLE, DESCRIPTION, HITS, ID) VALUES ('Data Structure', 'A data structure is a specialized format for organizing and storing data. General data structure types include the array, the file, the record, the table, the tree, and so on. Any data structure is designed to organize data to suit a specific purpose so that it can be accessed and worked with in appropriate ways.', null, 1);
INSERT INTO PORTAL.COURSE (TITLE, DESCRIPTION, HITS, ID) VALUES ('Database', 'A database is a collection of information that is organized so that it can be easily accessed, managed and updated.
INSERT INTO PORTAL.COURSE (TITLE, DESCRIPTION, HITS, ID) VALUES ('Java', 'Java is a general-purpose computer programming language that is concurrent, class-based, object-oriented,[14] and specifically designed to have as few implementation dependencies as possible. It is intended to let application developers "write once, run anywhere" (WORA),[15] meaning that compiled Java code can run on all platforms that support Java without the need for recompilation.', null, 3);
INSERT INTO PORTAL.COURSE (TITLE, DESCRIPTION, HITS, ID) VALUES ('C#', 'C# (pronounced "C sharp") is a programming language that is designed for building a variety of applications that run on the .NET Framework. C# is simple, powerful, type-safe, and object-oriented.', null, 4);
